import request from '@/utils/request';
export function login(data) {
  return request({
    url: '/user/login',
    method: 'post',
    data: data
  });
}
export function getInfo() {
  return request({
    url: '/user/info',
    method: 'get'
  });
}
export function logout() {
  return request({
    url: '/user/logout',
    method: 'get'
  });
}
export function getInviteUrl() {
  return request({
    url: '/user/invite',
    method: 'get'
  });
}
export function recharge(params) {
  return request({
    url: '/user/money',
    method: 'get',
    params: params
  });
}
