import axios from 'axios';
import { getInfo } from '@/api/user';
axios.defaults.withCredentials = true;
export default {
  name: 'Task',
  data: function data() {
    return {
      visible1: false,
      activeNames: ['1'],
      user: {
        user_invited_tick: 0,
        coin: 0
      },
      percentage: 0,
      status: ''
    };
  },
  created: function created() {
    this.requestData();
  },
  methods: {
    handleWriting: function handleWriting(couse_id, unit) {
      var _this = this;

      this.$confirm('确定开启第' + unit + '单元任务', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消'
      }).then(function () {
        _this.percentage = 0;
        _this.status = '';
        var timer = window.setInterval(function (e) {
          var step = Math.ceil((100 - _this.percentage) / 40);

          if (_this.percentage + step > 99) {
            _this.percentage = 99;
            clearInterval(timer);
          } else {
            _this.percentage += step;
          }
        }, 500);
        _this.visible1 = true;
        axios.get('http://127.0.0.1:3333/user/money', {
          params: {
            couse_id: couse_id,
            unit: unit,
            operate: 2
          }
        }).then(function (res) {
          if (res.data.status == 1) {
            _this.$message({
              message: '任务成功，已完成第' + unit + '单元',
              type: 'success'
            });

            clearInterval(timer);
            _this.percentage = 100;
            _this.status = 'success';
            window.setTimeout(function (e) {
              return _this.visible1 = false;
            }, 1000);

            _this.requestData();
          } else if (res.data.status == 0) {
            _this.status = 'exception';
            window.setTimeout(function (e) {
              return _this.visible1 = false;
            }, 1000);

            _this.$message({
              message: res.data.msg,
              type: 'warning'
            });
          } else {
            _this.visible1 = false;

            _this.$message.error('服务器异常，请稍后再试');
          }
        });
      }).catch(function () {});
    },
    requestData: function requestData() {
      var _this2 = this;

      getInfo().then(function (res) {
        var user = eval('(' + res.data + ')');
        _this2.user.coin = user.user_money;
        _this2.user.user_invited_tick = user.user_invited_tick;
      });
    }
  }
};
