import { queryPost, deletePost } from '@/api/post'

export default {
  name: 'Post',
  data() {
    return {
      data: [],
      selectUserId: [],
      queryOptions: {
        keyWords: '',
        page: 1,
        pageSize: 10
      },
      user: {},
      total: 0,
      loading: true,
      showCheckbox: false,
      showDialog: false
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    requestData() {
      queryPost(this.queryOptions).then((res) => {
        if (res.status === 0) {
          this.data = res.data.data
          this.total = res.data.total
          this.loading = false
        }
      })
    },
    handleSelect(select) {
      this.selectUserId = select.map((item) => {
        return item.id
      })
    },
    handleBatchDelete() {
      // if (this.selectMajorId.length < 1) {
      //   this.$message({
      //     type: 'warning',
      //     message: '请先选择要删除项'
      //   })
      //   return
      // }
      // this.$confirm(`确定要批量删除所选项吗？`, '提示', {
      //   type: 'warning'
      // }).then(async() => {
      //   const res = await this.api.batchDeleteMajor(this.selectMajorId)
      //   if (res.code === 0) {
      //     this.$message({
      //       type: 'success',
      //       message: '删除成功!'
      //     })
      //     this.requestData()
      //   }
      // })
    },
    handleDelete(id) {
      this.$confirm(`确定删除帖子吗？`, '提示', {
        type: 'warning'
      }).then(() => {
        deletePost(id).then((res) => {
          if (res.status === 0) {
            this.requestData()
            this.$message({
              type: 'success',
              message: res.msg
            })
          }
        })
      })
    },
    handleFilter() {
      this.queryOptions.page = 1
      this.requestData()
    },
    handlePersonInfo(user) {
      this.user = user
      this.showDialog = true
    },
    handleSizeChange(val) {
      this.queryOptions.pageSize = val
      this.requestData()
    },
    handleCurrentChange(val) {
      this.queryOptions.page = val
      this.requestData()
    }

  }

}
