import { getInviteUrl, getInfo } from '@/api/user'
export default {
  name: 'Invite',
  data() {
    return {
      invite_url: '',
      user: {
        user_invited_tick: 0,
        coin: 0
      }
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    handleClick() {
      getInviteUrl().then(res => {
        this.invite_url = 'http://autohomework.top/#/login?invite_code=' + res.data
      })
    },
    handleCopy() {
      const invite_url = document.getElementById('invite_url')
      invite_url.select()
      document.execCommand('Copy')
      this.$message({
        message: '已成功复制链接',
        type: 'success'
      })
    },
    requestData() {
      getInfo().then(res => {
        const user = eval('(' + res.data + ')')
        this.user.coin = user.user_money
        this.user.user_invited_tick = user.user_invited_tick
      })
    }
  }
}
