import { recharge, getInfo } from '@/api/user'
import { mapGetters } from 'vuex'
export default {
  name: 'Coin',
  data() {
    return {
      form: {
        card_name: '',
        card_password: '',
        operate: 1
      },
      rules: {
        card_name: [{ required: true, message: '请选择卡号', trigger: 'blur' }],
        card_password: [{ required: true, message: '请选择卡密', trigger: 'blur' }]
      },
      user: {
        user_invited_tick: 0,
        coin: 0,
        name:''
      }
    }
  },
  created() {
    this.requestData()
  }, computed: {
    ...mapGetters([
      'coin',
      'name'
    ])
  },
  methods: {
    onSubmit() {
      this.$refs['form'].validate((valid) => {
        if (valid) {
          recharge(this.form).then((res) => {
            this.$message({
              message: res.msg,
              type: 'success'
            })
            this.form={
                card_name: '',
                card_password: '',
                operate: 1
              }
            this.requestData()
          })
        } else {
          return false
        }
      })
    },
    requestData() {
      this.$store.dispatch('user/getInfo')
      // getInfo().then(res => {
      //   const user = eval('(' + res.data + ')')
      //   this.user.coin = user.user_money
      //   this.user.user_invited_tick = user.user_invited_tick
      //   this.user.name = user.user_name

      // })
    }
  }
}
