import { getInviteUrl,getInviteTotal } from '@/api/user'
export default {
  name: 'Invite',
  data() {
    return {
      invite_url: '',
      user: {
        user_invited_tick: 0,
      }
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    handleClick() {
      getInviteUrl().then(res => {
        this.invite_url = 'http://autohomework.top/#/login?invite_code=' + res.data.invite_code
      })
    },
    handleCopy() {
      const invite_url = document.getElementById('invite_url')
      invite_url.select()
      document.execCommand('Copy')
      this.$message({
        message: '已成功复制链接',
        type: 'success'
      })
    },
    requestData() {
      getInviteTotal().then(res =>{
          this.user.user_invited_tick = res.data.invite_total
      })
    }
  },
  created(){
       getInviteUrl().then(res => {
        this.invite_url = 'http://autohomework.top/#/login?invite_code=' + res.data.invite_code
      })
  }
}
