import { queryRotationChart, createRotationChart, putRotationChart, deleteRotationChart } from '@/api/rotation-chart'
export default {
  name: 'RotationChart',
  data() {
    return {
      data: [],
      selectUserId: [],
      queryOptions: {
        name: '',
        page: 1,
        pageSize: 20
      },
      editForm: {
        // name: '',
        type: 0,
        image: '',
        postId: '',
        url: '',
        priority: 0
      },
      rotationChart: {},
      loading: true,
      showDialog: false,
      rules: {
        // name: [{ required: true, message: '物品名称不能为空', trigger: 'blur' }],
        // type: [{ required: true, message: '请选择轮播图类型', trigger: 'blur' }],
        image: [{ required: true, message: '请上传图片', trigger: 'blur' }],
        priority: [{ required: true, message: '优先级不能为空', trigger: 'blur' }]
      }
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    requestData() {
      queryRotationChart({ name: this.queryOptions.name }).then((res) => {
        if (res.status === 0) {
          this.data = res.data
          this.loading = false
        }
      })
    },
    handleCreate() {
      this.rotationChart = {}
      this.resetForm()
      this.showDialog = true
    },
    handleImageSuccess(res, file) {
      if (res.status === 0) {
        this.editForm.image = 'https://xyzliu.cn:8000/img/' + res.data
      }
    },
    beforeImageUpload(file) {
      const isLt10M = file.size / 1024 / 1024 < 10
      if (!isLt10M) {
        this.$message.error('上传头像图片大小不能超过 2MB!')
      }
      return isLt10M
    },
    handleSave() {
      this.$refs['editForm'].validate((valid) => {
        if (!valid) {
          return false
        }
        if (this.rotationChart.id) {
          putRotationChart(this.editForm, this.rotationChart.id).then((res) => {
            if (res.status === 0) {
              this.resetForm()
              this.showDialog = false
              this.$message({
                type: 'success',
                message: '修改成功!'
              })
              this.requestData()
            }
          })
        } else {
          createRotationChart(this.editForm).then((res) => {
            if (res.status === 0) {
              this.showDialog = false
              this.$message({
                type: 'success',
                message: '添加成功!'
              })
              this.requestData()
            }
          })
        }
      }
      )
    },
    handleDelete(id) {
      this.$confirm(`确定删除吗？`, '提示', {
        type: 'warning'
      }).then(() => {
        deleteRotationChart(id).then((res) => {
          if (res.status === 0) {
            this.$message({
              type: 'success',
              message: '删除成功!'
            })
            this.requestData()
          }
        })
      })
    },
    resetForm() {
      this.editForm = {
        name: '',
        type: 0,
        image: '',
        postId: '',
        url: '',
        priority: 0
      }
    },
    handleFilter() {
      this.queryOptions.page = 1
      this.requestData()
    },
    handleEdit(rotationChart) {
      this.rotationChart = rotationChart
      this.editForm = {
        name: rotationChart.name,
        type: rotationChart.type,
        image: rotationChart.image,
        url: rotationChart.url,
        postId: rotationChart.postId,
        priority: rotationChart.priority
      }
      console.log(this.rotationChart)
      this.showDialog = true
    },
    handleSizeChange(val) {
      this.queryOptions.pageSize = val
      this.requestData()
    },
    handleCurrentChange(val) {
      this.queryOptions.page = val
      this.requestData()
    }

  }

}
