import { queryUser, forbidUser } from '@/api/manager'

export default {
  name: 'User',
  data() {
    return {
      data: [],
      selectUserId: [],
      queryOptions: {
        nickName: '',
        page: 1,
        pageSize: 10
      },
      user: {},
      total: 0,
      loading: true,
      showDialog: false
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    requestData() {
      queryUser(this.queryOptions).then((res) => {
        if (res.status === 0) {
          this.data = res.data.list
          this.total = res.data.total
          this.loading = false
        }
      })
    },
    handleSelect(select) {
      this.selectUserId = select.map((item) => {
        return item.id
      })
    },
    handleForbid(id) {
      this.$confirm(`确定封禁(解禁)用户吗？`, '提示', {
        type: 'warning'
      }).then(() => {
        forbidUser(id).then((res) => {
          if (res.status === 0) {
            this.requestData()
            this.$message({
              type: 'success',
              message: res.msg
            })
          }
        })
      })
    },
    handleFilter() {
      this.queryOptions.page = 1
      this.requestData()
    },
    handlePersonInfo(user) {
      this.user = user
      this.showDialog = true
    },
    handleSizeChange(val) {
      this.queryOptions.pageSize = val
      this.requestData()
    },
    handleCurrentChange(val) {
      this.queryOptions.page = val
      this.requestData()
    }

  }

}
