import { queryItemType, createItem, putItem, deleteItem } from '@/api/item-type'

export default {
  name: 'ItemType',
  data() {
    return {
      data: [],
      selectUserId: [],
      queryOptions: {
        name: '',
        page: 1,
        pageSize: 20
      },
      editForm: {
        name: ''
      },
      item: {},
      loading: true,
      showDialog: false,
      rules: {
        name: [{ required: true, message: '物品名称不能为空', trigger: 'blur' }]
      }
    }
  },
  created() {
    this.requestData()
  },
  methods: {
    requestData() {
      queryItemType(this.queryOptions).then((res) => {
        if (res.status === 0) {
          this.data = res.data
          this.loading = false
        }
      })
    },
    handleCreate() {
      this.item = {}
      this.resetForm()
      this.showDialog = true
    },
    handleSave() {
      this.$refs['editForm'].validate((valid) => {
        if (!valid) {
          return false
        }
        if (this.item.id) {
          putItem({ name: this.editForm.name, id: this.item.id }).then((res) => {
            if (res.status === 0) {
              this.resetForm()
              this.showDialog = false
              this.$message({
                type: 'success',
                message: '修改成功!'
              })
              this.requestData()
            }
          })
        } else {
          createItem(this.editForm).then((res) => {
            if (res.status === 0) {
              this.showDialog = false
              this.$message({
                type: 'success',
                message: '添加成功!'
              })
              this.requestData()
            }
          })
        }
      })
    },
    handleDelete(id) {
      this.$confirm(`确定删除吗？`, '提示', {
        type: 'warning'
      }).then(() => {
        deleteItem(id).then((res) => {
          if (res.status === 0) {
            this.$message({
              type: 'success',
              message: '删除成功!'
            })
            this.requestData()
          }
        })
      })
    },
    resetForm() {
      this.editForm = {
        name: ''
      }
    },
    handleFilter() {
      this.queryOptions.page = 1
      this.requestData()
    },
    handleEdit(item) {
      this.item = item
      this.editForm.name = item.name
      this.showDialog = true
    },
    handleSizeChange(val) {
      this.queryOptions.pageSize = val
      this.requestData()
    },
    handleCurrentChange(val) {
      this.queryOptions.page = val
      this.requestData()
    }

  }

}
